CustomField.formula:
IF(AND(RecordType.Name='Manual Order',OR(ISPICKVAL(Status,"Commande à facturer"),ISPICKVAL(Status,"Commande facturée"))), 
			True,
			AND(OR(ISPICKVAL(Status,"Commande à facturer"),ISPICKVAL(Status,"Commande facturée")),
							OR
        ( AND( MONTH( Effective_Start_Date__c ) <= MONTH(TODAY()),YEAR(Effective_Start_Date__c) = YEAR (TODAY()) ),
          YEAR(Effective_Start_Date__c) < YEAR (TODAY())
        )
				)			
)
