CustomField.formula:
IF( MID( TEXT(Index_Period__c) , 0 , 1) == 'M',
    DATE( Index_Year__c ,  VALUE(MID( TEXT(Index_Period__c) , 2 , LEN(TEXT(Index_Period__c)) -1)) , 1),
     IF( TEXT(Index_Period__c) == 'T1' , 
       DATE( Index_Year__c ,  1  , 1),
          IF( TEXT(Index_Period__c) == 'T2' , 
            DATE( Index_Year__c , 5  , 1),
              IF( TEXT(Index_Period__c) == 'T3' , 
                 DATE( Index_Year__c , 9  , 1),
                 DATE( Index_Year__c , 1  , 1)
               )
           )
     )
)
